import './App.css';
import { Component } from 'react';
import {  Redirect, BrowserRouter, Route, Link} from "react-router-dom"
import PrivateRoute from './PrivateRoute'

class register extends Component{
  render(){
    return(
    <div id="card">

    </div>
    );
  }
}

class enter extends Component{
  render(){
    return(
      <div>

      </div>
    );
  }
}

class main extends Component{
  constructor(props){
    super(props);
    this.state={
      email : "",
      password : ""
    };
    this.handleChange = this.handleChange.bind(this);
  }

  submit= () =>{
    const data = { "userEmail": this.state.email, "userPass": this.state.password };
    var request = require('sync-request');
    var res = request('POST', 'http://127.0.0.1:5000/logIn', {
      json: data,
    });
    var response = JSON.parse(res.getBody('utf8'));
    alert(JSON.stringify(response));
    if (response.message == "Success"){
      this.props.history.push("enter");
    }
    else{
      alert("User & Password don't match")
    }
  }

    request = async () => {
      const data = { "userEmail": this.state.email, "userPass": this.state.password };
      var success={"message":"Success"};
      let json=await fetch('http://127.0.0.1:5000/logIn', {
        body: JSON.stringify(data),
        headers: {
            "Content-Type": "application/json"
        },
        method: "POST"
        }) // https://cors-anywhere.herokuapp.com/https://example.com
        .then(response => response.text());
        let signUPJson=JSON.parse(json);

        // return json;
        if(JSON.stringify(signUPJson) === JSON.stringify(success)){
          this.props.history.push("enter");
        }
        else{
            alert("User is Already registered");

        }
      }



  handleChange(event) {
    // this.props.history.push("enter");
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
        [name]: value
    });
    console.log(this.state);
  }


  render(){
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }
    return(
    <div id="card">
      <div id="card-content">
        <div className="App">
          <div id="card-title">
            <h2>LOGIN</h2>
            <div class="underline-title"></div>
          </div>
          <form id="userForm" onSubmit = {this.submit} class="form">
            <label for="user-email" style={{paddingTop:"13px"}}>
                &nbsp;User Email
              </label>
            <input type ="text" value = {this.state.email} onChange={this.handleChange} class="form-content"  name="email" autocomplete="on" required />
            <div class="form-border"></div>
            <label for="user-password" style={{paddingTop:"22px"}}>&nbsp;Password
              </label>
            <input value = {this.state.password} onChange={this.handleChange} class="form-content" type="password" name="password" required />
            <div class="form-border"></div>
            <input id="submit-btn" type="submit" name="submit" value="LOGIN" />
            <a href="#" id="signup"></a>
          </form>
          <a href="register">
            or Register
          </a>
        </div>
      </div>
    </div>
    )
  }
}

class App extends Component{

  render(){
    return (
  <BrowserRouter>
    <Route path = "/" exact component = {main}/>
    <Route path = "/register" exact component = {register}/>
    <PrivateRoute path = "/enter" exact component = {enter}/>
  </BrowserRouter>
    );
  }
}

export default App;
