}

class main extends Component{
  constructor(props){
    super(props);
    this.state={
      email : "",
      password : ""
    };
    this.handleChange = this.handleChange.bind(this);
  }

  submit= () =>{
    const data = { "userEmail": this.state.email, "userPass": this.state.password };
    // alert(data);
    // let rawResponse = fetch('http://127.0.0.1:5000/logIn', {
    //   method: 'POST',
