import './App.css';
import { Component } from 'react';
import { BrowserRouter, Route, Link} from "react-router-dom"

class register extends Component{
  render(){
    return(
    <div id="card">

    </div>
    );
  }
}

class enter extends Component{
  render(){
    return(
      <div>
        aa
      </div>
    );
  }
}

class App extends Component{
  constructor(props){
    super(props);
    this.state={
      email : "",
      password : ""
    };
    this.handleChange = this.handleChange.bind(this);
  }

  submit= () =>{
    const data = { "userEmail": this.state.email, "password": this.state.password };

    fetch('http://localhost:5000/logIn', {
      method: 'POST', // or 'PUT'
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
        [name]: value
    });
    console.log(this.state);
  }

  render(){
    return (
  <BrowserRouter>
  <div id="card">
    <div id="card-content">
      <div className="App">
        <div id="card-title">
          <h2>LOGIN</h2>
          <div class="underline-title"></div>
        </div>
        <form id="userForm" onSubmit = {this.submit} class="form">
          <label for="user-email" style={{paddingTop:"13px"}}>
              &nbsp;User Email
            </label>
          <input type ="text" value = {this.state.email} onChange={this.handleChange} class="form-content"  name="email" autocomplete="on" required />
          <div class="form-border"></div>
          <label for="user-password" style={{paddingTop:"22px"}}>&nbsp;Password
            </label>
          <input value = {this.state.password} onChange={this.handleChange} class="form-content" type="password" name="password" required />
          <div class="form-border"></div>
          <input id="submit-btn" type="submit" name="submit" value="LOGIN" />
          <a href="#" id="signup"></a>
        </form>
        <a href="register">
          or Register
        </a>
      </div>
    </div>
    <Route path = "/register" exact component = {register}/>
    <Route path = "/enter" exact component = {enter}/>

  </div>
  </BrowserRouter>
    );
  }
}

export default App;
